<?phpinclude_once(APPLICATION_PATH.'/modules/admin/models/DbTable/Leave.php');class Admin_LdController extends Zend_Controller_Action{		public function viewleaveAction()	{		$session=new Zend_Session_Namespace();			if(isset($session->id))			{								$this->view->name="<b>".$session->name."</b>";				$data= new Model_DbTable_Leave();				$LeaveData=$data->viewLeave();				$this->view->resultviews = $LeaveData;				$this->render('viewleave');						}			else			{				$this->_helper->redirector('login','index');			}		}			public function leaveAction()	{		$session=new Zend_Session_Namespace();			if(isset($session->id))			{								$this->view->email="<b>".$session->email."</b>";								$LeaveId=$this->_request->getParam('leave_id');				$data= new Model_DbTable_Leave();								$leave=$data->leave($LeaveId);				$this->view->resultviews = $leave;				$this->render('leave');								}			else			{				$this->_helper->redirector('login','index');			}		}		public function deleteAction()	{		$session=new Zend_Session_Namespace();		if(isset($session->id))		{			$this->view->name="<b>".$session->name."</b>";									$data= new Model_DbTable_Leave();			$data->deleteLeave($LeaveId);			$this->_helper->redirector('viewleave','ld');			exit;		}		else		{			$this->_helper->redirector('login','index');		}			}		public function searchleaveAction()	{				$session=new Zend_Session_Namespace();			if(isset($session->id))			{				$this->view->name="<b>".$session->name."</b>";								$name=$this->getRequest()->getPost('name');								$data = new Model_DbTable_Leave();								$search=$data->searchLeave($name);												$this->view->resultviews=$search;								$this->render('searchleave1');							}			else			{				$this->_helper->redirector('login','index');			}	}		public function handleleaveAction()	{		$session=new Zend_Session_Namespace();			if(isset($session->id))			{				$this->view->name="<b>".$session->name."</b>";				$request=$this->getRequest();				$lid=$request->leave_id;					$status=$this->getRequest()->getPost('leave');							$data = new Model_DbTable_Leave();								$leaveStatus=$data->updateLeaveStatus($lid,$status);												$this->view->resultviews=$leaveStatus;								$this->_helper->redirector('viewleave', 'ld');exit;			}			else			{				$this->_helper->redirector('login','index');			}	}}