<?phpinclude_once(APPLICATION_PATH.'/modules/emp/models/DbTable/Salary.php');class Emp_EmpsalaryController extends Zend_Controller_Action{	public function viewsalaryAction()	{		$session=new Zend_Session_Namespace();		if(isset($session->id))		{			$this->view->name="<b>".$session->name."</b>";			//$data=new Model_DbTable_Salary();			//$SalaryData=$data->viewSalary();						$this->view->resultviews=$SalaryData;						$this->render('viewsalary');//print_r($SalaryData);exit;					}		else		{			$this->_helper->redirector('login','index');		}	}		public function searchsalaryAction()	{		$session=new Zend_Session_Namespace();		if(isset($session->id))		{			$this->view->name="<b>".$session->name."</b>";			$sm=$this->getRequest()->getPost('smonth');			$sy=$this->getRequest()->getPost('syear');			$sdate=$sy."-".$sm;						$data=new Model_DbTable_Salary();			$SalaryData=$data->viewSalary($sdate);						$this->view->resultviews=$SalaryData;			$this->render('viewsalary');					}		else		{			$this->_helper->redirector('login','index');		}	}	}