<?phpclass Model_DbTable_Salary extends Zend_Db_Table_Abstract{	protected $_name='salary';		public function viewSalary($sdate)	{		$session = new Zend_Session_Namespace(); 		if (isset($session->id)) 		{				$user = $session->id;						$select = $this->_db->select()								->from(array('salary'=>'salary'),array('salary_id','user_id','salarydate','basicsalary','days','medicalallowance','extraallowance',											 'grosspay','tax','tds','unpaidleave','netpay','bonus'))								->join(array('login'=>'login'),'salary.user_id=login.user_id',array('name','designation'))								->where('salarydate >\''.$sdate.'-00\' AND salarydate <\''.$sdate.'-31\' AND salary.user_id='.$user);											$resultsRows = $this->getAdapter()->fetchAll($select);						//Zend_Debug::dump($resultsRows);			return $resultsRows;		}				}}